UPDATE books
	SET book_count =
		(SELECT COUNT(*) FROM books_on_stock WHERE id_books = books.id GROUP BY id_books),
        updated_at = updated_at
	WHERE EXISTS 
		(SELECT id_books FROM books_on_stock WHERE id_books = books.id);

UPDATE users
	SET books_count =
		(SELECT COUNT(*) FROM books_on_stock WHERE received_from = users.id GROUP BY received_from)
	WHERE EXISTS 
		(SELECT received_from FROM books_on_stock WHERE received_from = users.id);

